import React, { useContext } from 'react';
import { NavLink } from 'react-router-dom';
import { CSSTransition, TransitionGroup } from 'react-transition-group'; //added
import { AuthContext } from '../../context/auth-context';
import './NavLinks.css';


const NavLinks = props => {
  const auth = useContext(AuthContext)
  return <ul className='nav-links'>
    <TransitionGroup component={null}> {/*added */}

    
    <CSSTransition timeout={200} classNames="fade">
    <li>
      <NavLink to='/places/boroughs'>ALL PLACES</NavLink>
    </li>
    </CSSTransition>
    <li>
      <NavLink to='/' exact>ALL USERS</NavLink>
    </li>
    {auth.isLoggedIn &&
    <li>
      <NavLink to={`/${auth.userId}/places`}>MY PLACES</NavLink>
    </li>}
    {auth.isLoggedIn &&
    <li>
      <NavLink to='/places/new'>NEW PLACES</NavLink>
    </li>}
    {!auth.isLoggedIn &&
    <li>
      <NavLink to='/auth'>AUTHENTICATE</NavLink>
    </li>}
    {auth.isLoggedIn &&
      <li><button onClick={auth.logout}>LOGOUT</button></li>
    }
  </TransitionGroup>  
  </ul>
}


export default NavLinks;
